+ (UIBezierPath*)emojiFacePathForSize:(CGSize)size {
    CGRect frame = CGRectFromSize(size);
    UIBezierPath* facePath = [UIBezierPath bezierPathWithOvalInRect:CGRectMake(CGRectGetMinX(frame) + floor(CGRectGetWidth(frame) * 0.04600 + 0.5), CGRectGetMinY(frame) + floor(CGRectGetHeight(frame) * 0.03600 - 0.5) + 1, floor(CGRectGetWidth(frame) * 0.96200 + 0.5) - floor(CGRectGetWidth(frame) * 0.04600 + 0.5), floor(CGRectGetHeight(frame) * 0.95200 - 0.5) - floor(CGRectGetHeight(frame) * 0.03600 - 0.5))];
    return facePath;
}

